import React, {useState} from 'react'

export default function TextForm(props) {

    const handleupclick=()=>{
        // console.log("Button clicked"+text);
        let newText=text.toUpperCase();
        setText(newText);
        props.showAlert("Text has converted to uppercase!","success");
    }

    const handlelowclick=()=>{
        // console.log("Button clicked"+text);
        let newText=text.toLowerCase();
        setText(newText);
        props.showAlert("Text has converted to lowercase!","success");
    }

    const cleartext=()=>{
        // console.log("Button clicked"+text);
        let newText="";
        setText(newText);
        props.showAlert("Your text has been clear!","success");
    }

    const copytext=()=>{
      let newText=text;
      setText(newText);
    }

    const handleonchange=(event)=>{
        // console.log("on change");
        setText(event.target.value);
    }

    // const[btntext,setBtntext]=useState("Dark Mode");

    // const[theme,setTheme]=useState({
    //     color: 'black',
    //     backgroundColor: 'white'
    // });

    // const changesmode=()=>{
    //     if(theme.color==='black'){
    //         setTheme({
    //             color: 'white',
    //             backgroundColor: 'black'
    //         })
    //         setBtntext("Light Mode");
    //     }
    //     else{
    //         setTheme({
    //             color: 'black',
    //             backgroundColor: 'white'
    //         })
    //     }
    // }

    
    const [text, setText]=useState("");
  return (
    <>
    <div className="container" style={{ color: props.mode==='light'?'black':'white'}}>
  <h2>{props.heading}</h2>
  <div className="mb-3">
    <textarea
      className="form-control"
      value={text}
      onChange={handleonchange}
      style={{
        backgroundColor: props.mode === 'dark' ? 'black' : 'white',
        color: props.mode === 'dark' ? 'white' : 'black',  // Added text color styling here
      }}
      id="myBox"
      rows="8"
    ></textarea>
  </div>
  <button className="btn btn-primary mx-1" onClick={handleupclick}>
    Convert to uppercase
  </button>
  <button className="btn btn-primary mx-1" onClick={handlelowclick}>
    Convert to lowercase
  </button>
  <button className="btn btn-primary mx-1" onClick={cleartext}>
    Clear text
  </button>
</div>

<div className="container my-4" style={{ color: props.mode==='light'?'black':'white'}}>
  <h2>Text summary</h2>
  <p>Your text has {text.split(' ').length} words & {text.length} characters</p>
  <p>{(0.008 * text.split(' ').filter((element)=>{return element.length!=0}).length).toFixed(2)} minutes required to read</p>
  <h2>Preview</h2>
  <p>{text.length>0?text:"Enter something into the above box to preview it here"}</p>
</div>
    </>
  )
}
