import React, {useState} from 'react'

export default function TextForm(props) {

    const handleupclick=()=>{
        // console.log("Button clicked"+text);
        let newText=text.toUpperCase();
        setText(newText);
    }

    const handlelowclick=()=>{
        // console.log("Button clicked"+text);
        let newText=text.toLowerCase();
        setText(newText);
    }

    const cleartext=()=>{
        // console.log("Button clicked"+text);
        let newText="";
        setText(newText);
    }

    const handleonchange=(event)=>{
        // console.log("on change");
        setText(event.target.value);
    }

    const[btntext,setBtntext]=useState("light");

    const[theme,setTheme]=useState({
        color: 'black',
        backgroundColor: 'white'
    });

    const changesmode=()=>{
        if(theme.color==='white'){
            setTheme({
                color: 'white',
                backgroundColor: 'black'
            })
            setBtntext("dark");
        }
        else{
            setTheme({
                color: 'black',
                backgroundColor: 'white'
            })
        }
    }

    
    const [text, setText]=useState("");
  return (
    <>
    <div className='container' style={theme}>
    <h2>{props.heading}</h2>
        <div className="mb-3" >
            <textarea className="form-control" value={text} onChange={handleonchange} id="exampleFormControlTextarea1" rows="8"></textarea>
        </div>
        <button className='btn btn-primary mx-1' onClick={handleupclick}>Convert to uppercase</button>
        <button className='btn btn-primary mx-1' onClick={handlelowclick}>Convert to lowercase</button>
        <button className='btn btn-primary mx-1' onClick={cleartext}>Clear text</button>
        <button className='btn btn-primary mx-1' onClick={changesmode}>{btntext}</button>
    </div>

    <div className="container" my-4 style={theme}>
        <h2>Text summary</h2>
        <p>Your text has {text.split(" ").length} words & {text.length} characters</p>
        <p>{0.008*text.split(" ").length} minutes requied to read</p>
        <h2>Preview</h2>
        <p>{text}</p>
    </div>
    </>
  )
}
